sp_widget.client_script:
function($rootScope,$scope) {
  /* widget controller */
  var c = this;
  c.selectItem = function(idx) {
    var id = c.data.notes[idx].sys_id;
    console.log('Note ID: ' + id);
    $rootScope.noteID = id;
    $rootScope.$emit('selectNote', id);
  }
}

sp_widget.script:
(function() {
  //create an array to populate with notes
  data.notes = [];
  var noteGR = new GlideRecord('x_58872_needit_needit');
  gs.info(gs.getUserID());
  noteGR.addQuery('u_requested_for', gs.getUserID());
  noteGR.orderByDesc('sys_created_on');
  noteGR.setLimit(options.maximum_records_to_display);
  noteGR.query();
  while (noteGR.next()) {
  var noteObj = {};
  //use service portal helper method to get some display values
  $sp.getRecordDisplayValues(noteObj, noteGR, 'number,u_requested_for,sys_id,short_description');
  //get the first 20 characters of the description
  //push the populated obj into the array
  data.notes.push(noteObj);
  }
  })();
