sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var what_needed = g_form.getValue('u_what_needed');
	g_form.clearOptions('u_what_needed');
   //Type appropriate comment here, and begin script below
   if(newValue=="hr"){
	   g_form.addOption('u_what_needed','hr1',"Human Resource Management1");
	   g_form.addOption('u_what_needed','hr2',"Human Resource Management2");
   }else{
	   g_form.addOption('u_what_needed','oth',"Others")
   }
}
