sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	checkDate : function(strDate){
		var rightnow = new GlideDateTime();
		// Create a GlideDateTime object for the When needed date
		var validateDate = new GlideDateTime(strDate);
		if(validateDate.before(rightnow)){
			return true;
		}else{
			return false;
		}
	},
    type: 'NeedItUtils'
};
